import React, { useState } from "react";
import ReactDOM from "react-dom";
import Carousel from "react-elastic-carousel";
import Grid from '@material-ui/core/Grid';
import Item from "./Item";

import "./Item.css";
import { PlaceData } from "../../PlaceData";

const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 550, itemsToShow: 2 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 4 },
];

function ItemParent() {

    const [displayPlace, setDisplayPlace] = useState(PlaceData[0].place);

    const [displayBody,setDisplayBody]=useState(PlaceData[0].bosy);
      


    

    const setData= (name,body) => {
        setDisplayBody(body);
        setDisplayPlace(name);
        
    }


    console.log('my name is tanivr', PlaceData);
    // const items = [
    //     { id: 1, title: 'item #1' },
    //     { id: 2, title: 'item #2' },
    //     { id: 3, title: 'item #3' },
    //     { id: 4, title: 'item #4' },
    //     { id: 5, title: 'item #5' }
    // ]

    return (
        <>
            <Grid md={12} direction="row" container justify="space-between" >
                <Grid item md={4} spacing={2}>
                    <div className="bannerLeft">

                        <h1>{displayPlace}</h1>
                        <p>{displayBody}</p>
                        <button style={{ width: '150px' }} className="btn btn-warning">Booking--></button>

                    </div>
                </Grid>

                <Grid item xs={12} md={8} className="bannrRight">
                    <Carousel breakPoints={breakPoints}>
                        {PlaceData.map(item => <Item key={item.id} setData={setData} place={item.place} body={item.body}></Item>)}
                    </Carousel>
                </Grid>



            </Grid>
        </>
    );
}
export default ItemParent;
