import React, { createContext, useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import CreateAccount from './Component/CreateAccount/CreateAccount';
import Hotel from './Component/Hotel/Hotel';
import ItemParent from './Component/Item/ItemParent';
import Login from './Component/Login/Login';
import Navbars from './Component/NavBar/Navbar';
import Notfound from './Component/Notfound/Notfound';
import PrivateRoute from './Component/PrivateRoute/PrivateRoute'


export const UserContext = createContext();

function App() {

  const [loggedInUser, setLoggedInUser] = useState({});
  const [detectPlace,setDetectPlace] = useState('');


  return (
    <UserContext.Provider value={[loggedInUser,setLoggedInUser]} className='container' >


      <Router>
  <h1>the place is {detectPlace}</h1>
       <Navbars></Navbars>
       <Switch>
        <Route exact path="/">
        <ItemParent setDetectPlace={setDetectPlace} ></ItemParent>
        </Route>
        <Route  path="/createAccount">
       <CreateAccount></CreateAccount>
        </Route>
        <Route  path="/login">
       <Login></Login>
        </Route>
        <PrivateRoute path="/hotel">
           <Hotel></Hotel>
          </PrivateRoute>
        <Route path="*">
            <Notfound></Notfound>
          </Route>
        {/* <Route path="/postDetail/:id">
          <PostDetail></PostDetail>
        </Route>
        <Route path="/post&comment/:id">
          <PostCommentControl></PostCommentControl>
        </Route> */}
      </Switch> 
    </Router>


    </UserContext.Provider>
  );
}

export default App;
