import React, { useContext, useEffect } from 'react';
import { UserContext } from '../../App';
import { PlaceData } from '../../PlaceData';
import './Hotel.css'
import HotelChild from './HotelChild';

const Hotel = (props) => {

    console.log(props.detectPlace);
    let data=[];

    useEffect(() => {
        data = PlaceData.filter(item =>{
            console.log(item.place);
            return item.place === props.detectPlace;

        } )
    }, [])

    console.log(PlaceData[0].hotel);
    return (

        <div class="container">
            <h3 style={{ color: 'white' }}>The hotel in {props.detectPlace}</h3>
            <hr />{
                data.map(item =>  <HotelChild item={item.hotel} ></HotelChild> )
            }
        </div>
    );
};

export default Hotel;
