import React from "react";
import ReactDOM from "react-dom";
import Carousel from "react-elastic-carousel";
import Grid from '@material-ui/core/Grid';
import Item from "./Item";
import "./Item.css";

const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 550, itemsToShow: 2 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 4 },
];

function ItemParent() {

    const items = [
        { id: 1, title: 'item #1' },
        { id: 2, title: 'item #2' },
        { id: 3, title: 'item #3' },
        { id: 4, title: 'item #4' },
        { id: 5, title: 'item #5' }
    ]

    return (
        <>
            <Grid sm={12} direction="row" container justify="space-between" >
                <Grid item md={4} spacing={2}>
                    <div className="bannerLeft">

                        <h1>my name is tanvir</h1>

                    </div>
                </Grid>


                <Grid item md={8} className="bannrRight">
                    <Carousel breakPoints={breakPoints}>
                        {items.map(item => <Item key={item.id}>{item.title}</Item>)}
                    </Carousel>
                </Grid>


            </Grid>
        </>
    );
}
export default ItemParent;
