import React, { useState } from "react";
import ReactDOM from "react-dom";
import Carousel from "react-elastic-carousel";
import Grid from '@material-ui/core/Grid';
import Item from "./Item";

import "./Item.css";
import { PlaceData } from "../../PlaceData";

const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 550, itemsToShow: 2 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 4 },
];

function ItemParent() {

    const [displayControl, setDisplayControl] = useState({
        place: PlaceData[0].place,
        body: PlaceData[0].body


    });

    const showData = (e) => {
        console.log('show data work',)
    }

    console.log('my name is tanivr', PlaceData);
    // const items = [
    //     { id: 1, title: 'item #1' },
    //     { id: 2, title: 'item #2' },
    //     { id: 3, title: 'item #3' },
    //     { id: 4, title: 'item #4' },
    //     { id: 5, title: 'item #5' }
    // ]

    return (
        <>
            <Grid md={12} direction="row" container justify="space-between" >
                <Grid item md={4} spacing={2}>
                    <div className="bannerLeft">

                        <h1>{displayControl.place}</h1>
                        <p>{displayControl.body}</p>
                        <button style={{ width: '150px' }} className="btn btn-warning">Booking--></button>

                    </div>
                </Grid>

                <Grid item xs={12} md={8} className="bannrRight">
                    <Carousel breakPoints={breakPoints}>
                        {PlaceData.map(item => <Item key={item.id} id={item.id} showData={showData}></Item>)}
                    </Carousel>
                </Grid>



            </Grid>
        </>
    );
}
export default ItemParent;
