import React, { useState } from 'react';
import './CreateAccount.css'

const CreateAccount = () => {
    const [checkPassWord,setCheckPassWord] =useState();
    
    const [user, setUser] = useState({
        isSignedIn: false,
        name: '',
        email: '',
        Conpassword:'',
        password: '',
        photo: ''
    });

    const handleBlur = (e) => {
        let isFieldValid = true;
        if(e.target.name === 'email'){
          isFieldValid = /\S+@\S+\.\S+/.test(e.target.value);
          
        }
        if(e.target.name === 'Conpassword'){
            isFieldValid = checkPassWord===e.target.value; 

        }
        if(e.target.name === 'password'){
          const isPasswordValid = e.target.value.length > 6;
          const passwordHasNumber =  /\d{1}/.test(e.target.value);
          isFieldValid = isPasswordValid && passwordHasNumber;
          setCheckPassWord(e.target.value)
        }
        if(isFieldValid){
          const newUserInfo = {...user};
          newUserInfo[e.target.name] = e.target.value;
          setUser(newUserInfo);
        }
      }

    //   const handleSubmit = (e) => {
    //     if(newUser && user.email && user.password){
    //       createUserWithEmailAndPassword(user.name, user.email, user.password)
    //       .then(res => {
    //         handleResponse(res, true);
    //       })
    //     }
    
    //     if(!newUser && user.email && user.password){
    //       signInWithEmailAndPassword(user.email, user.password)
    //       .then(res => {
    //         handleResponse(res, true);
    //       })
    //     }
    //     e.preventDefault();
    //   }
    return (
          
       
            <div className=" container loginSection">

                <form class="login-area ">
                    <h3>Create an account</h3>
                    <div>
                        <input type="text" onBlur={handleBlur} name="Fname" placeholder="Firsst name" />
                    </div>
                    <div>
                        <input type="text" onBlur={handleBlur} name="Lname" placeholder="Last name" />
                    </div>
                    <div>
                        <input type="text" onBlur={handleBlur} name="email" placeholder="User name or email" />
                    </div>
                    <div>
                        <input type="password" onBlur={handleBlur} name="password" placeholder="password" />
                    </div>
                    <div>
                        <input type="password" onBlur={handleBlur} name="Conpassword" placeholder="Confirm password" />
                    </div>

                    <br />

                    <button class="bttn">Create an account</button>
                    <p>Allready have an account?<a href="#">Log in</a></p>

                </form>
                <br />
                <div class="d-flex fakeBorder bottomPart">
                    <hr />
                    <span >or</span>
                    <hr />
                </div>
                <div class="bottomPart">
                    <button class="bttnGf"><img src="https://www.logo.wine/a/logo/Facebook/Facebook-f_Logo-Blue-Logo.wine.svg" />
                                Continue with facebook
                                </button>
                    <br />
                    <button class="bttnGf">
                        <img src="https://i.pinimg.com/originals/39/21/6d/39216d73519bca962bd4a01f3e8f4a4b.png" />
                                            Continue with Google</button>
                </div>
            </div>
    );
};

export default CreateAccount;
